package com.knight.gen01;

public class Student {
	private String name;
	private String no;
	private int score;
	
	public Student() {
		// TODO Auto-generated constructor stub
	}
	
	public Student(String name, String no, int score) {
		super();
		this.name = name;
		this.no = no;
		this.score = score;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public String getno() {
		return no;
	}

	public void setno(String no) {
		this.no = no;
	}

	public int getScore() {
		return score;
	}

	public void setScore(int score) {
		this.score = score;
	}

	
	
	

}
